function selectItem(){$(".item--js").click(function(){var e=$(this);if(!e.hasClass("item--disabled")){$(e).toggleClass("item--active");var t=e.children(".item__info").children(".item__title--js");t.toggleClass("item__title--color"),t.text("Сказочное заморское яство"),e.children(".item__description--js").toggleClass("hidden")}})}function hoverItem(){$(".item--js").mouseenter(function(){var e=$(this);if(e.hasClass("item--active")){var t=e.children(".item__info").children(".item__title--js");t.addClass("item__title--color"),t.text("Котэ не одобряет?")}}),$(".item--js").mouseleave(function(){var e=$(this),t=e.children(".item__info").children(".item__title--js");e.hasClass("item--active")&&(t.removeClass("item__title--color"),t.text("Сказочное заморское яство"))})}$(document).ready(function(){selectItem(),hoverItem()});